mapper.select:
select count(*)
from member
where nickName=#{nickName}

select count(*)
from member
where USER_ID=#{id}

mapper.update:
update member
set PASSWORD=#{pwd} , NICKNAME=#{nickName}, phone=#{phone}, address=#{address}
WHERE USER_ID = #{id}

mapper.select:
select user_id
	from member
	where email = #{email}

select user_id
	from member
	where email = #{email} and USER_ID=#{id}

mapper.update:
update member
set PASSWORD=#{pwd}
WHERE USER_ID = #{id}
